settings.data:
{
    var temps = [];
    for(var i=0;i<50;i++){
        var dt = new Date(datasources["KKMakerTemp"]["with"][i]["created"]);
        //if(dt.getHours()<=21 && dt.getDate()<8 && dt.getMonth()<=10 && dt.getYear()<=2015){
        //    continue;
        //}
        temps.push([dt.toString(),datasources["KKMakerTemp"]["with"][i]["content"]["temp"]]);
    } 
    return [temps];
}

settings.options:
{
  series: [ {color:'orange'} ],
  animate: true,
  animateReplot: true,
  cursor: {
            show: true,
            zoom: true,
            looseZoom: true,
            showTooltip: false
  },
  title:'Temperature History',
    axes:{
        xaxis:{
            renderer:$.jqplot.DateAxisRenderer,
            tickOptions:{formatString:'%b %#d, %#I %p'},
			min:'November 08, 2015 8:00PM', 
			tickInterval:'1 hours'	            
        },
        yaxis: {
        	tickOptions: {
                    formatString: "%'d*C"
            }
        }
    }
}
